INSERT INTO pizzas (nome, descrizione, foto, prezzo) VALUES ('Margherita', 'La classica pizza italiana con pomodoro fresco, mozzarella fusa e basilico appena colto.', '/img/margherita.jpg', 6.50), ('Diavola', 'Pomodoro, mozzarella e salame piccante che accende il palato con un tocco di peperoncino.', '/img/diavola.jpg', 7.50), ('Quattro Formaggi', 'Un tripudio di formaggi: mozzarella, gorgonzola, fontina e parmigiano su base bianca.', '/img/quattro_formaggi.jpg', 8.00), ('Capricciosa', 'Pomodoro, mozzarella, prosciutto cotto, carciofi, olive e funghi per un gusto completo.', '/img/capricciosa.jpg', 8.50), ('Bufalina', 'Pomodoro fresco e mozzarella di bufala DOP, condita con basilico e olio EVO.', '/img/bufalina.jpg', 9.00), ('Vegetariana', 'Un mix colorato di verdure grigliate: zucchine, melanzane, peperoni e cipolla.', '/img/vegetariana.jpg', 7.80), ('Tonno e Cipolla', 'Pomodoro, mozzarella, tonno sottâ€™olio e cipolla rossa dolce per un gusto deciso.', '/img/tonno_cipolla.jpg', 7.20), ('Prosciutto e Funghi', 'Una combinazione perfetta tra dolcezza del prosciutto cotto e sapore dei funghi.', '/img/prosciutto_funghi.jpg', 7.90), ('Napoli', 'Pomodoro, mozzarella, acciughe, capperi e origano per i puristi del sud.', '/img/napoli.jpg', 7.00), ('Wurstel e Patatine', 'Mozzarella, wurstel tagliati e patatine fritte per un gusto irresistibile.', '/img/wurstel_patatine.jpg', 6.90), ('Boscaiola', 'Funghi porcini, salsiccia, mozzarella e panna per un sapore ricco e avvolgente.', '/img/boscaiola.jpg', 9.50), ('Frutti di Mare', 'Pomodoro, frutti di mare misti e prezzemolo su base leggera e profumata.', '/img/frutti_mare.jpg', 10.00), ('Tartufo', 'Mozzarella, crema al tartufo nero e funghi porcini per un gusto elegante.', '/img/tartufo.jpg', 11.00), ('Zucchine e Speck', 'Mozzarella, zucchine a fette sottili e speck croccante per un tocco affumicato.', '/img/zucchine_speck.jpg', 8.70), ('Calzone Farcito', 'Calzone ripieno di pomodoro, mozzarella, prosciutto e ricotta, cotto al forno.', '/img/calzone_farcito.jpg', 9.20);
